func TestData(t *testing.T) {
	for _, data := range datas {
		pys := Gains(data)
		for _, py := range pys {
			fmt.Print(py.String())
		}
		fmt.Println()
	}
}

func TestGain(t *testing.T) {
	py := Gain("你好")
	t.Log("source：", py.Source())
	t.Log("initial：", py.Initial())
	t.Log("finals：", py.Finals())
	t.Log("tone：", py.Tone())
	t.Log("string：", py.String())
	t.Log("stringTone：", py.StringTone())
	t.Log("error：", py.Err())
	t.Log("----------------------------------")
	py2 := Gain("你")
	t.Log("source：", py2.Source())
	t.Log("initial：", py2.Initial())
	t.Log("finals：", py2.Finals())
	t.Log("tone：", py2.Tone())
	t.Log("string：", py2.String())
	t.Log("stringTone：", py2.StringTone())
	t.Log("error：", py2.Err())
}

func TestGains(t *testing.T) {
	pinyins := Gains("你好")
	for _, py := range pinyins {
		t.Log("source：", py.Source())
		t.Log("initial：", py.Initial())
		t.Log("finals：", py.Finals())
		t.Log("tone：", py.Tone())
		t.Log("string：", py.String())
		t.Log("stringTone：", py.StringTone())
		t.Log("error：", py.Err())
		t.Log("----------------------------------")
	}
}

func TestToString(t *testing.T) {
	pinyins := Gains("你好噢")
	t.Log(ToString(pinyins, ""))
	t.Log(ToString(pinyins))
	t.Log(ToString(pinyins, ","))
	t.Log(ToString(pinyins, "-"))
}

func TestToStringTone(t *testing.T) {
	pinyins := Gains("你好噢")
	t.Log(ToStringTone(pinyins, ""))
	t.Log(ToStringTone(pinyins))
	t.Log(ToStringTone(pinyins, ","))
	t.Log(ToStringTone(pinyins, "-"))
}
